// components/EditHospital.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const EditHospital = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [hospitalData, setHospitalData] = useState({
        rating: '',
        image: '',
        description: '',
        numberOfDoctors: '',
        numberOfDepartments: ''
    });

    useEffect(() => {
        const fetchHospitalData = async () => {
            const response = await fetch(`/api/hospitals/${id}`);
            const data = await response.json();
            setHospitalData(data);
        };
        fetchHospitalData();
    }, [id]);

    const handleChange = (e) => {
        setHospitalData({ ...hospitalData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        await fetch(`/api/hospitals/${id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(hospitalData),
        });
        navigate(`/hospital/${id}`); // Redirect to hospital detail page
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Edit Hospital Details</h2>
            <input type="text" name="rating" value={hospitalData.rating} onChange={handleChange} placeholder="Rating" />
            <input type="text" name="image" value={hospitalData.image} onChange={handleChange} placeholder="Image URL" />
            <textarea name="description" value={hospitalData.description} onChange={handleChange} placeholder="Description" />
            <input type="number" name="numberOfDoctors" value={hospitalData.numberOfDoctors} onChange={handleChange} placeholder="Number of Doctors" />
            <input type="number" name="numberOfDepartments" value={hospitalData.numberOfDepartments} onChange={handleChange} placeholder="Number of Departments" />
            <button type="submit">Update Hospital</button>
        </form>
    );
};

export default EditHospital;
