import React, { useState } from 'react';

const HospitalForm = () => {
    const [name, setName] = useState('');
    const [city, setCity] = useState('');
    const [imageUrl, setImageUrl] = useState('');
    const [specialities, setSpecialities] = useState([]);
    const [rating, setRating] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        // Handle form submission logic here
        console.log({ name, city, imageUrl, specialities, rating });
    };

    const specialityOptions = ['Cardiology', 'Neurology', 'Pediatrics', 'Orthopedics', 'Oncology'];

    return (
        <div className="form-container">
            <h2>Add New Hospital</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" placeholder="Hospital Name" value={name} onChange={(e) => setName(e.target.value)} required />
                <input type="text" placeholder="City" value={city} onChange={(e) => setCity(e.target.value)} required />
                <input type="url" placeholder="Image URL" value={imageUrl} onChange={(e) => setImageUrl(e.target.value)} required />
                <select multiple value={specialities} onChange={(e) => setSpecialities([...e.target.selectedOptions].map(option => option.value))}>
                    {specialityOptions.map((speciality) => (
                        <option key={speciality} value={speciality}>{speciality}</option>
                    ))}
                </select>
                <input type="number" placeholder="Rating (1-5)" value={rating} onChange={(e) => setRating(e.target.value)} min="1" max="5" required />
                <button type="submit">Add Hospital</button>
            </form>
        </div>
    );
};

export default HospitalForm;
