import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import SignIn from './components/SignIn';
import SignUp from './components/SignUp';
import HospitalForm from './components/HospitalForm';
import './App.css';

const App = () => {
    const [isSignIn, setIsSignIn] = useState(true); // State to toggle between Sign In and Sign Up

    return (
        <Router>
            <div className="App">
                <nav>
                    <Link to="/" onClick={() => setIsSignIn(true)}>Sign In</Link>
                    <Link to="/" onClick={() => setIsSignIn(false)}>Sign Up</Link>
                    <Link to="/add-hospital">Add Hospital</Link>
                </nav>
                <Routes>
                    <Route path="/" element={<>{isSignIn ? <SignIn setIsSignIn={setIsSignIn} /> : <SignUp setIsSignIn={setIsSignIn} />}</>} />
                    <Route path="/add-hospital" element={<HospitalForm />} />
                </Routes>
            </div>
        </Router>
    );
};

export default App;
